sql.where:
  			and O_PROD_NAME like '%${oProdName}%'
			and O_CUS_NAME like '%${oCusName}%'
			and O_CUS_ATRB_NAME like '%${oCusAtrbName}%'
			and O_TACTIC like '%${oTactic}%'
			and O_STATE=#{oState}
			and O_PACT_STATE=#{oPactState}
			and O_BESPEAK_TIME >= #{beginBespeakTime}
			and O_BESPEAK_TIME <= #{endBespeakTime}
			and O_BEOK_TIME >= #{beginBeokTime}
			and O_BEOK_TIME <= #{endBeokTime}
			and O_PACT_STATE >= #{beginTransferTime}
			and O_PACT_STATE <= #{endTransferTime}
			and RC_STATE=0 order by CREATE_DATE desc
