sql.where:
  			and PRODUCT_NAME like '%${productName}%'
			and STRATEGY_NAME like '%${strategyName}%'
			and CHN_NAME like '%${chnName}%'
			and AGENT_NAME like '%${agentName}%'
			and CONTACT_STATUS=#{contactStatus}
			and ORDER_STATUS=#{orderStatus}
			and PREORDER_CREATETIME >= #{beginPreorderCreatetime}
			and PREORDER_CREATETIME <= #{endPreorderCreatetime}
			and PREORDER_COMPLETETIME >= #{beginPreorderCompletetime}
			and PREORDER_COMPLETETIME <= #{endPreorderCompletetime}
			and PAY_TIME >= #{beginPayTime}
			and PAY_TIME <= #{endPayTime}
			and RC_STATE=0 order by CREATE_DATE desc
